export function validateName(text){
    const regx = RegExp(/^[a-z ,.'-]+$/i);
    return !regx.test(text)
}

export function validateEmail(text){
    const regx = RegExp(/^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/);
    return !regx.test(text)
}

export function validatePassword(text,min,max){
    let result = !text || text.length < min; 
    if(max){
        result = result || text.length > max;
    }
    return result;
}

export function validateConfirmPass(entPass, cnfPass){
    if(entPass === cnfPass){
        return false;
    }else{
        return true;
    }
}
